import React from "react";
import './App.css';

function App() {

	return (
		<div className="App">
			<header className="App-header">
				<button onClick={() => addServer()}>Add server</button>
				{JsonDataDisplay(getServers())}
			</header>
		</div>
	);
}

async function getServers() {
	const response = await fetch("http://localhost:3001/get", {
		method: 'GET',
		mode: 'cors',
		headers: { 'Content-Type': 'application/json' },
	});
	const data = await response.json();

	return data;
}
async function updateServer() {

}
async function addServer() {
	const response = await fetch("http://localhost:3001/add", {
		method: 'POST',
		mode: 'cors',
		headers: { 'Content-Type': 'application/json' },
		body: JSON.stringify({
			_IP: "127.0.0.1",
			_Name: "Server1",
			_TypeName: "t1.micro",
			_TypePPM: 1
		})
	});
	const data = await response.json();
	console.log(data);

	return data;
}

function JsonDataDisplay(jsondata) {
	const DisplayData= jsondata.map(
		(info) => {
			return(
				<tr>
					<td>{info.id}</td>
					<td>{info.name}</td>
					<td>{info.city}</td>
				</tr>
			)
		}
	)

	return(
		<div>
			<table class="table table-striped">
				<thead>
					<tr>
					<th>Sr.NO</th>
					<th>Name</th>
					<th>City</th>
					</tr>
				</thead>
				<tbody>
					{DisplayData}
				</tbody>
			</table>
		</div>
	)
}

export default App;
