import React, { Fragment, useEffect, useState } from "react";

import EditServer from "./EditServer";
import DeleteServer from "./DeleteServer";

function ListServers() {
	const [servers, setServers] = useState([]);
	const [counter, setCounter] = useState(0);

	const getServers = async () => {
		try {
			const response = await fetch("http://localhost:3001/get");
			const data = await response.json();

			setServers(data);
		} catch (err) {
			console.error(err.message);
		}
	};

	const deleteServer = async (id) => {
		try {
			const response = await fetch("http://localhost:3001/delete/" + id, {
				method: "DELETE",
			});

			setServers(servers.filter((server) => server._id !== id));
		} catch (err) {
			console.error(err.message);
		}
	};

	const toggleServer = async (server) => {
		try {
			const body = {
				_IsRunning: !server._isrunning,
			};
			const res = await fetch("http://localhost:3001/toggle/" + server._id, {
				method: "PUT",
				headers: { "Content-Type": "application/json" },
				body: JSON.stringify(body),
			});

			window.location = "/";
		} catch (err) {
			console.error(err.message);
		}
	};

	useEffect(() => {
		getServers();
	}, []);

	useEffect(() => {
		const interval = setInterval(() => {
			console.log("Counter: " + counter);
			setCounter(counter + 1);
		}, 1000);

		return () => clearInterval(interval);
	}, []);

	return (
		<Fragment>
			<table className="table mt-5 text-center">
				<thead>
					<tr>
						<th>Name</th>
						<th>IP</th>
						<th>Time Running</th>
						<th>Type</th>
						<th>Price So Far</th>

						<th>Toggle</th>
						<th>Edit</th>
						<th>Delete</th>
					</tr>
				</thead>
				<tbody>
					{servers.map((server) => (
						<tr
							key={server._id}
							className={
								server._isrunning ? "bg-success text-white" : "bg-light"
							}
						>
							<td>{server._name}</td>
							<td>{server._ip}</td>
							<td>
								{() => {
									let secs =
										server._timerunning.minutes * 60 +
										server._timerunning.seconds +
										counter;
									let hrs = Math.floor(secs / 3600);
									let mins = Math.floor(secs / 60);
									let sec = secs % 60;
									console.log(secs);
									return hrs + ":" + mins + ":" + sec;
								}}
							</td>
							<td>{server._title}</td>
							<td>
								{server._timerunning.minutes * server._ppm +
									server._timerunning.seconds * (server._ppm / 60)}
							</td>

							<td>
								<button
									id="toggle"
									className="btn btn-warning"
									onClick={() => toggleServer(server)}
								>
									{server._isrunning ? "Turn Off" : "Turn On"}
								</button>
							</td>
							<td>
								<EditServer server={server} />
							</td>
							<td>
								<button
									id="delete"
									className="btn btn-danger"
									onClick={() => deleteServer(server._id)}
								>
									Delete
								</button>
							</td>
						</tr>
					))}
				</tbody>
			</table>
		</Fragment>
	);
}

export default ListServers;
