import React, {Fragment} from "react";
import './App.css';

function App() {

	return (
		<div className="App">
			<header className="App-header">
				<button onClick={() => addServer()}>Add server</button>
			</header>
			<div>
				{JsonDataDisplay(getServers())}
			</div>
		</div>
	);
}

async function getServers() {
	const response = await fetch("http://localhost:3001/get", {
		method: 'GET',
		mode: 'cors',
		headers: { 'Content-Type': 'application/json' },
	});
	const res = await response.json();
	console.log(res);

	return res;
}
async function updateServer(data) {
	const response = await fetch("http://localhost:3001/update", {
		method: 'PUT',
		mode: 'cors',
		headers: { 'Content-Type': 'application/json' },
		body: JSON.stringify(data)
	});
	const res = await response.json();
	console.log(res);

	return res;
}
async function addServer(data) {
	const response = await fetch("http://localhost:3001/add", {
		method: 'POST',
		mode: 'cors',
		headers: { 'Content-Type': 'application/json' },
		body: JSON.stringify(data)
	});
	const res = await response.json();
	console.log(res);

	return res;
}
async function deleteServer(id) {
	const response = await fetch("http://localhost:3001/delete", {
		method: 'DELETE',
		mode: 'cors',
		headers: { 'Content-Type': 'application/json' },
		body: JSON.stringify({
			_ID: id
		})
	});
	const data = await response.json();
	console.log(data);

	return data;
}

function JsonDataDisplay(info) {
	let jsxResult = "";

	for (let i = 0; i < info.length; i++) {
		jsxResult += `<div>${info[i]._Name}</div>`;
		console.log(info[i]._Name);
	}

	return jsxResult;
}

export default App;
