import React from "react";
import './App.css';

function App() {

	return (
		<div className="App">
			<header className="App-header">
				<button onClick={() => addServer()}>Add server</button>
				{JsonDataDisplay(getServers())}
			</header>
		</div>
	);
}

async function getServers() {
	const response = await fetch("http://localhost:3001/get", {
		method: 'GET',
		mode: 'cors',
		headers: { 'Content-Type': 'application/json' },
	});
	const data = await response.json();
	console.log(data);
	console.log(typeof data);

	return data;
}
async function updateServer() {

}
async function addServer() {
	const response = await fetch("http://localhost:3001/add", {
		method: 'POST',
		mode: 'cors',
		headers: { 'Content-Type': 'application/json' },
		body: JSON.stringify({
			_IP: "127.0.0.1",
			_Name: "Server1",
			_TypeName: "t1.micro",
			_TypePPM: 1
		})
	});
	const data = await response.json();
	console.log(data);

	return data;
}

function JsonDataDisplay(info) {
	return (
		<div>
			{info.array.forEach(server => (
				<div key={server._ID}>
					<p>{server._ip}</p>
					<p>{server._name}</p>
					<p>{server._type[0]}</p>
					<p>{server._type[1]}</p>
					<p>{server._isrunning}</p>
					<p>{server._timerunning}</p>
				</div>
			))}
		</div>
	);
}

export default App;
