import React, { Fragment, useEffect, useState } from "react";

import EditServer from "./EditServer";

function ListServers() {
	const [servers, setServers] = useState([]);

	const getServers = async () => {
		try {
			const response = await fetch("http://localhost:3001/get");
			const data = await response.json();

			setServers(data);
		} catch (err) {
			console.error(err.message);
		}
	};

	const deleteServer = async (id) => {
		try {
			const response = await fetch(URL + "delete/" + id, {
				method: "DELETE",
			});

			setServers(servers.filter((server) => server._id !== id));
		} catch (err) {
			console.error(err.message);
		}
	};

	useEffect(() => {
		getServers();
	}, []);

	return (
		<Fragment>
			<table className="table mt-5 text-center">
				<thead>
					<tr>
						<th>Name</th>
						<th>IP</th>
						<th>Time Running</th>
						<th>Type</th>
						<th>Price So Far</th>

						<th>Toggle</th>
						<th>Edit</th>
						<th>delete</th>
					</tr>
				</thead>
				<tbody>
					{servers.map((server) => (
						<tr key={server._id}>
							<td>{server._name}</td>
							<td>{server._ip}</td>
							<td>{server._timerunning}</td>
							<td>{server._title}</td>
							<td>{server._timerunning * server._ppm}</td>

							<td>
								<button className="btn btn-warning">
									{server._isrunning ? "Turn Off" : "Turn On"}
								</button>
							</td>
							<td>
								<EditServer server={server} />
							</td>
							<td>
								<button
									className="btn btn-danger"
									onClick={() => deleteServer(server._id)}
								>
									Delete
								</button>
							</td>
						</tr>
					))}
				</tbody>
			</table>
		</Fragment>
	);
}

export default ListServers;
