import React, { Fragment, useState } from "react";

const URL = "http://localhost:3001/";

function AddServer()
{
	const [serverName, setServerName] = useState("");
	const [serverIP, setServerIP] = useState("");
	const [serverTypeName, setServerTypeName] = useState("");
	const [serverTypePPM, setServerTypePPM] = useState("");


	const handleSubmit = async(e) => {
		e.preventDefault();
		try {
			const body = { _Name: serverName, _IP: serverIP, _TypeName: serverTypeName, _TypePPM: serverTypePPM };
			const response = await fetch(URL + "add", {
				method: "POST",
				headers: { "Content-Type": "application/json" },
				body: JSON.stringify(body)
			});

			window.location("/")
		} catch (err) { console.error(err.message); }
	}

	return (
		<Fragment>
		<h1 className="text-center mt-5">Servers</h1>
		<form className="d-flex mt-5" onSubmit={handleSubmit}>
			<input type="text" className="form-control" value={serverName} onChange={e => setServerName(e.target.value)} />
			<input type="text" className="form-control" value={serverIP} onChange={e => setServerIP(e.target.value)} />
			<input type="text" className="form-control" value={serverTypeName} onChange={e => setServerTypeName(e.target.value)} />
			<input type="text" className="form-control" value={serverTypePPM} onChange={e => setServerTypePPM(e.target.value)} />
			<button className="btn btn-success">Add</button>
		</form>
		</Fragment>
	);
}

export default AddServer;
